INSERT INTO academic_projects (role, company, date, description, link, image, technologies) VALUES
('Researcher', 'A Personalized Spatiotemporal Human Activity Recognition Framework in an Edge-Based Federated Learning Approach', 'Academic Research', 'Drafted an IEEE-format paper analyzing prior research on human activity recognition, federated learning, edge computing, and spatiotemporal data. Proposed a framework integrating spatiotemporal data into a human activity recognition system using edge-based federated learning, supported by a detailed presentation.', '#', '/images/projects/har-federated.png', ARRAY['Mendeley', 'Google Scholar', 'Microsoft Word', 'Microsoft PowerPoint']),
('Researcher', 'Salary Data Prediction and Analysis Using Machine Learning', 'GitHub', 'Developed a predictive model for tech employee salaries using four years of Stack Overflow data. Implemented and compared ML models (Linear Regression, Decision Tree, Random Forest, Gradient Boosting, XGBoost) using MAE, MSE, and R² metrics. Integrated the model into production for real-time predictions via Streamlit and visualized salary trends pre- and post-pandemic with Matplotlib, supported by detailed documentation.', 'https://github.com/wafafatima66/Prediction-analysis-ML', '/images/projects/salary-prediction.png', ARRAY['Python', 'Matplotlib', 'Numpy', 'Pandas', 'Pickle', 'Sklearn', 'Streamlit', 'Git', 'Jupyter Notebook']),
('Researcher', 'Cognitive Science and Artificial Intelligence', 'Academic Research', 'Authored an APA-format literature review on AI creativity, rationality, and theory of mind, synthesizing research, assessing methodologies, and identifying gaps in the field.', '#', '/images/projects/cognitive-ai.png', ARRAY['APA Style', 'Google Scholar', 'Microsoft Word']);
